import React from "react";
import { Link as RouterLink } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import { useTheme } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import IconButton from "@material-ui/core/IconButton";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Divider from "@material-ui/core/Divider";
import Tooltip from "@material-ui/core/Tooltip";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ForumIcon from "@material-ui/icons/Forum";
import MenuBookIcon from "@material-ui/icons/MenuBook";
import EqualizerIcon from "@material-ui/icons/Equalizer";
import AccountBalanceWalletIcon from "@material-ui/icons/AccountBalanceWallet";
import SettingsIcon from "@material-ui/icons/Settings";
import "../styles/LeftDrawer.css";

const DRAWER_WIDTH = 58;

// TODO: Remove styling with CSS
const useStyles = makeStyles((theme) => ({
  toolbar: {
    fontSize: theme.typography.body2,
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  drawerPaper: {
    position: "relative",
    width: DRAWER_WIDTH,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
    "&::-webkit-scrollbar": {
      display: "none",
    },
  },
}));

function routerStyle(location, icon) {
  return {
    color: location === icon ? "#536dfe" : "#757575",
  };
}

function MainListIcons(props) {
  return (
    <div>
      <ListItem>
        <ListItemIcon>
          <RouterLink to="/forum" style={routerStyle(props.location, "forum")}>
            <Tooltip
              title="Forum"
              placement="right"
              className={props.classes.tooltip}
              arrow
            >
              <ForumIcon className="forum-icon" />
            </Tooltip>
          </RouterLink>
        </ListItemIcon>
      </ListItem>
      <ListItem>
        <ListItemIcon>
          <RouterLink to="/guide" style={routerStyle(props.location, "guide")}>
            <Tooltip
              title="Guide"
              placement="right"
              className={props.classes.tooltip}
              arrow
            >
              <MenuBookIcon className="guide-icon" />
            </Tooltip>
          </RouterLink>
        </ListItemIcon>
      </ListItem>
      <ListItem>
        <ListItemIcon>
          <RouterLink to="/trade" style={routerStyle(props.location, "trade")}>
            <Tooltip
              title="Trade"
              placement="right"
              className={props.classes.tooltip}
              arrow
            >
              <EqualizerIcon className="trade-icon" />
            </Tooltip>
          </RouterLink>
        </ListItemIcon>
      </ListItem>
      <ListItem>
        <ListItemIcon>
          <RouterLink
            to="/wallet"
            style={routerStyle(props.location, "wallet")}
          >
            <Tooltip
              title="Wallet"
              placement="right"
              className={props.classes.tooltip}
              arrow
            >
              <AccountBalanceWalletIcon className="wallet-icon" />
            </Tooltip>
          </RouterLink>
        </ListItemIcon>
      </ListItem>
    </div>
  );
}

function SecondaryListIcons(props) {
  return (
    <div>
      <ListItem>
        <ListItemIcon>
          <RouterLink
            to="/settings"
            style={routerStyle(props.location, "settings")}
          >
            <Tooltip
              title="Settings"
              placement="right"
              className={props.classes.tooltip}
              arrow
            >
              <SettingsIcon className="settings-icon" />
            </Tooltip>
          </RouterLink>
        </ListItemIcon>
      </ListItem>
    </div>
  );
}

export default function LeftDrawer({
  renderDrawer,
  location,
  handleDrawerClose,
}) {
  const theme = useTheme();
  const classes = useStyles(theme);

  if (renderDrawer) {
    return (
      <div className="left-drawer">
        <Drawer
          variant="permanent"
          classes={{
            paper: classes.drawerPaper,
          }}
          anchor="left"
        >
          <div className={classes.toolbarIcon}>
            <IconButton onClick={handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Divider />
          <div classsName="drawer-list-icons">
            <List className="main-list-icons" autowidth={false}>
              <MainListIcons classes={classes} location={location} />
            </List>
            <List className="secondary-list-icons" autowidth={false}>
              <SecondaryListIcons classes={classes} location={location} />
            </List>
          </div>
        </Drawer>
      </div>
    );
  }
  return null;
}
